// ============================================
// AGGREGATE BY FILE (METADATA PRESERVATION)
// ============================================

const items = $input.all();

// Dosyalara g√∂re grupla
const fileGroups = {};

items.forEach(item => {
  const filename = item.json.metadata?.filename || 'unknown';

  if (!fileGroups[filename]) {
    fileGroups[filename] = {
      filename: filename,
      elements: [],
      totalElements: 0
    };
  }

  fileGroups[filename].elements.push({
    text: item.json.text || '',
    type: item.json.type || 'UnknownType',
    pageNumber: item.json.metadata?.page_number,
    parentId: item.json.metadata?.parent_id,
    elementId: item.json.element_id
  });

  fileGroups[filename].totalElements++;
});

console.log(`üìÅ Grouped ${items.length} elements into ${Object.keys(fileGroups).length} files`);

// Her dosya i√ßin detay
Object.entries(fileGroups).forEach(([filename, data]) => {
  console.log(`   ${filename}: ${data.totalElements} elements`);
});

// Output: Her dosya bir item olarak
return Object.values(fileGroups).map(fileData => ({
  json: fileData
}));

// ============================================
// CHUNK DOCUMENTS (STRUCTURE-AWARE)
// ============================================

const files = $input.all();

// Chunking parametreleri
const CHUNK_CONFIG = {
  maxTokens: 500,
  overlap: 100,
  preserveTitles: true
};

// Basit token estimator
function estimateTokens(text) {
  return Math.ceil(text.split(/\s+/).length * 1.3);
}

// Chunking function
function createChunks(elements, filename) {
  const chunks = [];
  let currentChunk = {
    text: '',
    elements: [],
    metadata: {
      filename: filename,
      types: new Set(),
      pages: new Set(),
      hasTitle: false,
      elementCount: 0
    }
  };

  elements.forEach((element) => {
    const isTitle = element.type === 'Title';
    const currentTokens = estimateTokens(currentChunk.text);
    const elementTokens = estimateTokens(element.text);

    // Yeni chunk ba≈ülat?
    const shouldStartNewChunk = currentChunk.text && (
      (CHUNK_CONFIG.preserveTitles && isTitle) ||
      (currentTokens + elementTokens > CHUNK_CONFIG.maxTokens)
    );

    if (shouldStartNewChunk) {
      // Mevcut chunk'ƒ± kaydet
      chunks.push({
        text: currentChunk.text.trim(),
        metadata: {
          ...currentChunk.metadata,
          types: Array.from(currentChunk.metadata.types),
          pages: Array.from(currentChunk.metadata.pages).sort((a,b) => a-b),
          tokenCount: estimateTokens(currentChunk.text),
          chunkIndex: chunks.length
        }
      });

      // Yeni chunk (overlap ile)
      const overlapElements = currentChunk.elements.slice(-2);
      currentChunk = {
        text: overlapElements.map(e => e.text).join('\n\n'),
        elements: overlapElements,
        metadata: {
          filename: filename,
          types: new Set(),
          pages: new Set(),
          hasTitle: false,
          elementCount: 0
        }
      };
    }

    // Element ekle
    currentChunk.text += (currentChunk.text ? '\n\n' : '') + element.text;
    currentChunk.elements.push(element);
    currentChunk.metadata.types.add(element.type);
    if (element.pageNumber) currentChunk.metadata.pages.add(element.pageNumber);
    if (isTitle) currentChunk.metadata.hasTitle = true;
    currentChunk.metadata.elementCount++;
  });

  // Son chunk
  if (currentChunk.text.trim()) {
    chunks.push({
      text: currentChunk.text.trim(),
      metadata: {
        ...currentChunk.metadata,
        types: Array.from(currentChunk.metadata.types),
        pages: Array.from(currentChunk.metadata.pages).sort((a,b) => a-b),
        tokenCount: estimateTokens(currentChunk.text),
        chunkIndex: chunks.length
      }
    });
  }

  return chunks;
}

// ============================================
// Her dosyayƒ± chunk'la
// ============================================

const allChunks = [];
let totalChunks = 0;

files.forEach(file => {
  const fileData = file.json;
  const filename = fileData.filename;
  const elements = fileData.elements;

  console.log(`\nüìÑ ${filename}`);
  console.log(`   Elements: ${elements.length}`);

  const chunks = createChunks(elements, filename);
  console.log(`   ‚úÖ Chunks: ${chunks.length}`);

  chunks.forEach((chunk, idx) => {
    allChunks.push({
      json: {
        content: chunk.text,
        metadata: chunk.metadata,
        filename: filename,
        chunkId: `${filename}_chunk_${idx}`,
        totalChunksInFile: chunks.length
      }
    });
  });

  totalChunks += chunks.length;
});

console.log(`\nüéâ TOTAL: ${totalChunks} chunks from ${files.length} files`);

return allChunks;

