// Pinecone'dan gelen sonu√ßlarƒ± al
const pineconeData = $input.first().json;
const matches = pineconeData.matches || pineconeData[0]?.matches || [];

// Query'yi webhook'tan al
const query = $('Knowledge Search - RAG Query').first().json.body.query;

// THRESHOLD EKLE
const minScore = 0.5;
const relevantMatches = matches.filter(match => match.score >= minScore);

// Eƒüer sonu√ß yoksa VEYA t√ºm sonu√ßlar d√º≈ü√ºk score'sa
if (!matches || matches.length === 0 || relevantMatches.length === 0) {
  return [{
    json: {
      context: "No relevant information found.",
      sources: [],
      results_count: 0,
      query: query
    }
  }];
}

// RELEVANT MATCHES kullan (matches yerine)
const context = relevantMatches
  .map((match, index) => {
    const text = match.metadata.content || '';
    const filename = match.metadata.filename || 'Unknown';
    const score = match.score.toFixed(3);

    return `[Source ${index + 1}] (Relevance: ${score}, File: ${filename})\n${text}`;
  })
  .join('\n\n---\n\n');

// Sources listesi olu≈ütur
const sources = relevantMatches.map(match => ({
  filename: match.metadata.filename,
  chunk_id: match.id,
  score: match.score,
  chunk_index: match.metadata.chunkIndex
}));

// Output
return [{
  json: {
    context: context,
    sources: sources,
    results_count: relevantMatches.length,
    query: query
  }
}];

// Format Context'ten direkt al
const formatContext = $('Format Context').first().json;

// Generate Answer'dan al
const answerNode = $input.first().json;

// ‚≠ê user_id ve platform'u webhook'tan al (dinamik!)
const webhookData = $('Knowledge Search - RAG Query').first().json.body;
const userId = webhookData.user_id || "anonymous";  // Fallback
const platform = webhookData.platform || "api";     // ‚≠ê YENƒ∞!

// Model ismini al
const modelUsed = answerNode.model || "gpt-4o-mini";

// Sources
const sources = formatContext.sources || [];
const scores = sources.map(s => s.score);
const averageScore = scores.length > 0 
  ? (scores.reduce((sum, s) => sum + s, 0) / scores.length).toFixed(3)
  : 0;
const topScore = scores.length > 0 ? Math.max(...scores).toFixed(3) : 0;
const lowestScore = scores.length > 0 ? Math.min(...scores).toFixed(3) : 0;
const sourceFilenames = [...new Set(sources.map(s => s.filename))];

// Answer content
const answerContent = answerNode.message?.content || "";

// metadata objesi
const searchMetadata = {
  model: modelUsed,
  average_score: parseFloat(averageScore),
  top_score: parseFloat(topScore),
  lowest_score: parseFloat(lowestScore),
  sources: sourceFilenames,
  chunks_used: formatContext.results_count || sources.length,
  answer_length: answerContent.length,
  timestamp: new Date().toISOString()
};

// analytics_data objesi
const analyticsData = {
  query: formatContext.query,
  results_found: formatContext.results_count || sources.length,
  model_used: modelUsed,
  answer_length: answerContent.length,
  average_relevance: parseFloat(averageScore),
  sources_used: sourceFilenames,
  platform: platform  // ‚≠ê YENƒ∞!
};

// Output - user_id ve platform eklendi!
return [{
  json: {
    query: formatContext.query,
    results_count: formatContext.results_count,
    user_id: userId,      // ‚≠ê Dinamik!
    platform: platform,   // ‚≠ê YENƒ∞!
    metadata: searchMetadata,
    analytics_data: analyticsData,
    answer: answerContent,
    model: modelUsed
  }
}];

