// Extract conversation data from webhook
const input = $input.first().json;

// UUID helper function
function isValidUUID(str) {
  const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
  return uuidRegex.test(str);
}

function generateUUID() {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    const r = Math.random() * 16 | 0;
    const v = c === 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
}

// String temizleme fonksiyonu - N8N expression karakterlerini temizler
function cleanString(str) {
  if (!str || typeof str !== 'string') return str;
  // "=" ile ba≈ülƒ±yorsa kaldƒ±r
  if (str.startsWith('=')) {
    str = str.substring(1);
  }
  return str.trim();
}

// Check body first, then top level
let conversationId = input.body?.conversation_id ||
                     input.conversation_id || 
                     input.body?.thread_ts ||
                     input.thread_ts || 
                     input.body?.session_id ||
                     input.session_id;

// If not a valid UUID, generate one
if (!conversationId || !isValidUUID(conversationId)) {
  conversationId = generateUUID();
}

// Get user info - temizle
let userId = input.body?.user_id ||
             input.user_id || 
             input.user?.id || 
             'anonymous';
userId = cleanString(userId);

// Get platform
const platform = input.body?.platform || input.platform || 'api';

// Get query - BODY i√ßinde question varsa onu al ve temizle
let query = input.body?.question ||   // IVR: body.question
            input.body?.query ||      // IVR: body.query
            input.query ||             // Slack: top level query
            input.text ||              // Slack: text
            input.message ||           // Genel: message
            '';
query = cleanString(query);

return {
  json: {
    conversation_id: conversationId,
    user_id: userId,
    platform: platform,
    query: query,
    original_input: input
  }
};

// Get conversation history from previous node
const messages = $input.all().map(item => item.json);

// Sort by created_at (oldest first)
messages.sort((a, b) => new Date(a.created_at) - new Date(b.created_at));

// Format for LLM
const formattedHistory = messages.map(msg => {
  return `${msg.role}: ${msg.content}`;
}).join('\n\n');

return {
  json: {
    conversation_history: formattedHistory,
    message_count: messages.length
  }
};
